--
-- Name: ee_booking__delete(integer); Type: FUNCTION; Schema: public; Owner: evex
--

CREATE FUNCTION public.ee_booking__delete(integer) RETURNS integer
    LANGUAGE plpgsql
    AS $_$
declare
     v_item_id	    alias for $1;
     v_num_children integer;
begin
