--

CREATE FUNCTION public.im_boolean_from_id(integer) RETURNS character varying
    LANGUAGE plpgsql
    AS $_$
DECLARE
	p_boolean	alias for $1;
	v_result	varchar;
BEGIN
	v_result := p_boolean;
	IF 1 = p_boolean THEN v_result := 'true'; END IF;
	IF 0 = p_boolean THEN v_result := 'false'; END IF;
	return v_result;
END;$_$;


