CREATE FUNCTION public.im_email_from_user_id(integer) RETURNS character varying
    LANGUAGE plpgsql
    AS $_$
DECLARE
	v_user_id	alias for $1;
	v_email varchar(100);
BEGIN
	select email
	into v_email
	from parties
	where party_id = v_user_id;

	return v_email;
END;$_$;


