CREATE FUNCTION public.lob_get_data(p_lob_id integer) RETURNS text
    LANGUAGE plpgsql
    AS $$
DECLARE
        v_rec   record;
        v_data  text default '';
BEGIN
        for v_rec in select data, segment from lob_data where lob_id = p_lob_id order by segment 
        loop
            v_data := v_data || v_rec.data;
        end loop;

        return v_data;

END;
$$;


