--
-- Name: acs_users_all; Type: VIEW; Schema: public; Owner: evex
--

CREATE VIEW public.acs_users_all AS
 SELECT pa.party_id,
    pa.email,
    pa.url,
    pe.person_id,
    pe.first_names,
    pe.last_name,
    pe.bio,
    u.user_id,
    u.authority_id,
    u.username,
    u.screen_name,
    u.priv_name,
    u.priv_email,
    u.email_verified_p,
    u.email_bouncing_p,
    u.no_alerts_until,
    u.last_visit,
    u.second_to_last_visit,
    u.n_sessions,
    u.password,
    u.salt,
    u.password_question,
    u.password_answer,
    u.password_changed_date,
    u.auth_token
   FROM public.parties pa,
    public.persons pe,
    public.users u
  WHERE ((pa.party_id = pe.person_id) AND (pe.person_id = u.user_id));


