--
-- Name: all_photo_images; Type: VIEW; Schema: public; Owner: evex
--

CREATE VIEW public.all_photo_images AS
 SELECT i.item_id,
    ccr.relation_tag,
    im.image_id,
    im.width,
    im.height,
    p.pa_photo_id,
    p.caption,
    p.story,
    p.user_filename,
    p.camera_model,
    p.date_taken,
    p.flash,
    p.focal_length,
    p.exposure_time,
    p.aperture,
    p.focus_distance,
    p.metering,
    p.sha256,
    p.photographer
   FROM public.cr_items i,
    public.cr_items i2,
    public.pa_photos p,
    public.cr_child_rels ccr,
    public.images im
  WHERE ((i.item_id = ccr.parent_id) AND (p.pa_photo_id = i.live_revision) AND (ccr.child_id = i2.item_id) AND (i2.live_revision = im.image_id));


