    rel_side character(3) DEFAULT 'two'::bpchar NOT NULL,
    required_rel_segment integer NOT NULL,
    CONSTRAINT rel_constraints_rel_side_ck CHECK ((rel_side = ANY (ARRAY['one'::bpchar, 'two'::bpchar])))
);


