        CASE
            WHEN (c.relkind = 'r'::"char") THEN 'TABLE'::text
            WHEN (c.relkind = 'v'::"char") THEN 'VIEW'::text
            ELSE (c.relkind)::text
        END AS table_type,
    d.description AS comments
   FROM (pg_class c
     LEFT JOIN pg_description d ON ((c.oid = d.objoid)))
  WHERE (d.objsubid = 0);


